mapper.select:
	SELECT aa.* FROM (   	
   		SELECT


	) aa


	LIMIT #{rowNumToShow} OFFSET #{startRnumForMysql}

SELECT
	seq
	,type
	,name
	,zipCode
	,address
	,addressDetail
	,sido
	,tel
	,closedTime
	,openTime
	,adultPrice
	,childPrice
	,detailText
FROM
	spot
WHERE 1=1
	AND seq = #{seq}
		AND type = #{type}
			AND name = #{name} 
		AND zipCode = #{zipCode}
		AND address = #{address}
		AND addressDetail = #{addressDetail}
		AND sido = #{sido}
		AND tel = #{tel}
		AND closedTime = #{closedTime}
		AND openTime = #{openTime}
		AND adultPrice = #{adultPrice}
		AND childPrice = #{childPrice}
		AND detailText = #{detailText}

SELECT aa.* FROM(
select


GROUP BY a.seq

HAVING 1=1
AND starRating <= #{starRatingMax} AND starRating >= #{starRatingMin} 
OR starRating is null

order by avg(c.starRating) DESC
order by avg(c.starRating) 
) aa
LIMIT #{rowNumToShow} OFFSET #{startRnumForMysql}

SELECT


	AND a.seq = #{seq}

GROUP BY a.seq

SELECT count(seq) cnt FROM ( SELECT a.seq, ROUND(avg(c.starRating),1) starRating
GROUP BY a.seq HAVING 1=1 AND starRating <= #{starRatingMax} AND starRating >= #{starRatingMin} OR starRating is null
) as aa

mapper.insert:
    insert into `spot` (
    	type,
    	name,
    	zipCode,
    	address,
    	addressDetail,
    	sido,
    	tel,
    	openTime,
    	closedTime,
		adultPrice,
		childPrice,
		detailText
	) values (
		#{type}
		,#{name}
		,#{zipCode}
		,#{address}
		,#{addressDetail}
		,#{sido}
		,#{tel}
		,#{openTime}
		,#{closedTime}
		,#{adultPrice}
		,#{childPrice}
		,#{detailText}
	)

		SELECT last_insert_id()
