mapper.select:
SELECT
	seq
	,type
	,name
	,zipCode
	,address
	,addressDetail
	,sido
	,tel
	,closedTime
	,openTime
	,adultPrice
	,childPrice
	,detailText
FROM
	spot
WHERE 1=1
	AND seq = #{seq}
		AND type = #{type}
			AND name = #{name} 
		AND zipCode = #{zipCode}
		AND address = #{address}
		AND addressDetail = #{addressDetail}
		AND sido = #{sido}
		AND tel = #{tel}
		AND closedTime = #{closedTime}
		AND openTime = #{openTime}
		AND adultPrice = #{adultPrice}
		AND childPrice = #{childPrice}
		AND detailText = #{detailText}

	SELECT aa.* FROM (   	
   		SELECT


	) aa


	LIMIT #{rowNumToShow} OFFSET #{startRnumForMysql}

mapper.insert:
    insert into `spot` (
    	type,
    	name,
    	zipCode,
    	address,
    	addressDetail,
    	sido,
    	tel,
    	openTime,
    	closedTime,
		adultPrice,
		childPrice,
		detailText
	) values (
		#{type}
		,#{name}
		,#{zipCode}
		,#{address}
		,#{addressDetail}
		,#{sido}
		,#{tel}
		,#{openTime}
		,#{closedTime}
		,#{adultPrice}
		,#{childPrice}
		,#{detailText}
	)

		SELECT last_insert_id()
