    insert into `spot` (
    	seq,
    	type,
    	name,
    	zipCode,
    	address,
    	addressDetail,
    	sido,
    	tel,
    	openTime,
    	closedTime,
		adultPrice,
		childPrice,
		detailText
	) values (
		#{seq}
		,#{type}
		,#{name}
		,#{zipCode}
		,#{address}
		,#{addressDetail}
		,#{sido}
		,#{tel}
		,#{openTime}
		,#{closedTime}
		,#{adultPrice}
		,#{childPrice}
		,#{detailText}
	)

		SELECT last_insert_id()
